"use strict";

var _ = require("..");

describe('parseIntParam', function () {
  it('should handle strings', function () {
    expect((0, _.parseIntParam)('1', 0)).toBe(1);
  });
  it('should prevent negative numbers', function () {
    expect((0, _.parseIntParam)('-1', 0)).toBe(0);
  });
  it('should handle defaults', function () {
    expect((0, _.parseIntParam)(null, 10)).toBe(10);
  });
});
describe('parseSQONParam', function () {
  it('should handle defaults', function () {
    var obj = {
      op: 'and',
      content: []
    };
    expect((0, _.parseSQONParam)(null, obj)).toBe(obj);
  });
});